<script>
    import axios from "axios";

    export default {
        name: "IndiaMap",
        data(){
            return {
                stateWise: {}
            }
        },
        mounted() {
            this.axiosInstance = axios.create({
                timeout: 50000,
                headers: {
                    'Content-Type': 'application/json',
                }
            });
            this.fetchDataStateWise();
            document.getElementsByTagName('select')[0].selectedIndex = 0;
        },
        data(){
            return {
                statToShow: 'confirmed',
                color_swatch:{
                    confirmed: ['#eff3ff','#bdd7e7','#6baed6','#3182bd','#08519c'],
                    recovered: ['#edf8e9','#bae4b3','#74c476','#31a354','#006d2c'],
                    deaths: ['#f1eef6','#d7b5d8','#df65b0','#dd1c77','#980043'],
                },
                confirmed_cases: {},
                deaths_cases: {},
                recovered_cases: {},
                all:{}
            }
        },
        methods:{
            fetchDataStateWise(){
                const self = this;
                this.axiosInstance.get('/data/india/state-wise')
                    .then(response =>{
                        for(let i = 0; i < response.data.data.regional.length; i++) {
                            response.data.data.regional[i]['recovered'] = response.data.data.regional[i]['discharged']
                            response.data.data.regional[i]['confirmed'] = response.data.data.regional[i]['confirmedCasesIndian'] + response.data.data.regional[i]['confirmedCasesForeign']
                        }
                        self.$data.all = response.data.data.regional;
                        self.paintMap('confirmed')

                    }).catch(error=>{
                    console.log(error)
                })
            },

            paintMap(statToShow){
                if(['confirmed', 'recovered', 'deaths'].includes(statToShow)) {
                    let color_swatch = this.$data.color_swatch[statToShow];
                    this.$data.all.forEach(state_data => {
                        console.log(state_data[statToShow] + ' ' + state_data.loc)
                        let color_index = this.getColorIndex(state_data[statToShow]);
                        // color the graph
                        if(state_data.loc === 'Telengana') state_data.loc = 'Telangana';
                        let state = document.querySelectorAll('[title="' + state_data.loc + '"]')[0];

                        if(state) {
                            state.style.fill = color_swatch[color_index];
                        } else {
                            //console.log(country_data.country_code + ' coded country not found')
                        }
                    });

                }
            },

            getColorIndex(number_of_cases){
                if (number_of_cases < 1) {
                    number_of_cases = 1
                }
                let color_index = Math.floor(Math.log(number_of_cases) / Math.LN10 + 0.000000001);
                if(color_index > 4){
                    return 4
                } else {
                    return color_index
                }
            },

            handleMouseLeave(){
                document.getElementById("tooltip").classList.remove("active");
            },

            handleMouseMove(event) {
                let stateId = event.target.id;
                let stateName = document.getElementById(stateId).getAttribute('title');

                let state_index = this.$data.all.findIndex(function(item, i){
                    return item.loc === stateName
                });
                let data = {};
                if(state_index === -1){
                    data.country = stateName;
                    data.confirmed = 0;
                    data.recovered = 0;
                    data.deaths = 0;
                } else {
                    data.country = stateName;
                    data.confirmed = this.$data.all[state_index].confirmed ? this.$data.all[state_index].confirmed : 0;
                    data.recovered = this.$data.all[state_index].recovered  ? this.$data.all[state_index].recovered : 0;
                    data.deaths = this.$data.all[state_index].deaths  ? this.$data.all[state_index].deaths : 0;
                }



                let tooltip = document.getElementById("tooltip");
                let x = event.clientX;
                let y = event.clientY;

                let htmlStr = `<span class="has-text-weight-bold has-text-right">
                                CNTRY
                                </span>
                                <br>
                                <span class="confirmed-color">
                                    Confirmed: CNF
                                </span>
                                <br>
                                <span class="recovered-color">
                                    Recovered: RCV
                                </span>
                                <br>
                                <span class="deaths-color">
                                    Deaths: DUD
                                </span>`;
                htmlStr = htmlStr.replace('CNF', data.confirmed)
                    .replace('RCV', data.recovered)
                    .replace('DUD', data.deaths)
                    .replace('CNTRY', data.country);

                tooltip.style.left = (x + 20) + "px";
                tooltip.style.top = (y - 20) + "px";
                tooltip.innerHTML =  htmlStr;
                if(data.country) {
                    tooltip.classList.add("active");
                }

            },
        },
    }
</script>

